mapper.select:
select
comment.*, uer.name as userName
from comment
left join user on comment.user_id = user.id

     and comment.id = #{id}
     and comment.content like concat('%', #{content}, '%')
     and comment.user_id = #{userId}
     and comment.pid = #{pid}
     and time like concat('%', #{time}, '%')
     and comment.fid = #{fid}
     and module like concat('%', #{module}, '%')
     and comment.root_id = #{rootId}

order by id desc

select comment.*, uer.name as userName, user.avatar as avatar from comment left join user on comment.user_id = user.id
comment.pid is null and fid = #{fid} and module = #{module}
order by id desc

select c1.*, user1.name as userName, user1.avatar as avatar, user2.name as parentUserName from comment c1 left join user user1 on c1.user_id = user1.id left join comment c2 on c1.pid = c2.pid left join user user2 on c2.user_id = user2.id
c1.pid is not null and c1.root_id = #{rootId}
order by c1.id desc
